-- ----------------------------
--  Table structure for `th_ads`
-- ----------------------------
DROP TABLE IF EXISTS `th_ads`;
CREATE TABLE `th_ads` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `adtype` smallint(5) unsigned NOT NULL DEFAULT '0',
  `position` smallint(5) unsigned NOT NULL DEFAULT '0',
  `subject` char(255) NOT NULL,
  `linkurl` char(255) NOT NULL,
  `pathname` char(255) NOT NULL,
  `coverpic` char(255) NOT NULL,
  `cityid` char(20) NOT NULL,
  `begindate` int(10) unsigned NOT NULL,
  `enddate` int(10) unsigned NOT NULL,
  `publish` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `weight` tinyint(3) unsigned NOT NULL DEFAULT '5',
  `filter` text NOT NULL,
  `width` smallint(5) unsigned NOT NULL DEFAULT '0',
  `height` smallint(5) unsigned NOT NULL DEFAULT '0',
  `dcode` text NOT NULL,
  `publock` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `sortby` smallint(5) unsigned NOT NULL DEFAULT '0',
  `pageview` int(10) unsigned NOT NULL DEFAULT '0',
  `clicks` int(10) unsigned NOT NULL DEFAULT '0',
  `createtime` int(10) unsigned NOT NULL,
  `addon_cityid` char(10) NOT NULL,
  `match_addon_cityid` char(40) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `type_position` (`position`,`adtype`),
  FULLTEXT KEY `subject` (`subject`)
) ENGINE=MyISAM AUTO_INCREMENT=24 DEFAULT CHARSET=utf8;

