-- ----------------------------
--  Table structure for `th_arcdata`
-- ----------------------------
DROP TABLE IF EXISTS `th_arcdata`;
CREATE TABLE `th_arcdata` (
  `aid` int(10) unsigned NOT NULL,
  `module_id` smallint(5) unsigned NOT NULL,
  `channel_id` smallint(3) unsigned NOT NULL DEFAULT '0',
  `monthview` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `weekview` smallint(5) unsigned NOT NULL DEFAULT '0',
  `todayview` smallint(5) unsigned NOT NULL DEFAULT '0',
  `activetime` int(10) unsigned NOT NULL,
  `expiretime` int(10) unsigned NOT NULL,
  `publock` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `begindate` int(10) unsigned NOT NULL DEFAULT '0',
  `enddate` int(10) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`aid`,`module_id`,`channel_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `th_archives`
-- ----------------------------
DROP TABLE IF EXISTS `th_archives`;
CREATE TABLE `th_archives` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `channel_id` smallint(5) unsigned NOT NULL,
  `cid` char(30) NOT NULL,
  `sid` char(30) NOT NULL,
  `cityid` char(30) NOT NULL,
  `subject` char(200) NOT NULL,
  `subtitle` char(200) NOT NULL,
  `coverpic` char(200) NOT NULL,
  `author` char(30) NOT NULL,
  `match_cityid` char(120) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_mc` (`channel_id`),
  FULLTEXT KEY `ft_cityid` (`match_cityid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `th_arcindex`
-- ----------------------------
DROP TABLE IF EXISTS `th_arcindex`;
CREATE TABLE `th_arcindex` (
  `aid` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `module_id` smallint(5) unsigned NOT NULL,
  `channel_id` smallint(5) unsigned NOT NULL,
  `uid` int(10) unsigned NOT NULL,
  `root_uid` int(10) unsigned NOT NULL COMMENT '允许操作的其它用户，管理员总是允许操作',
  `totalview` int(10) unsigned NOT NULL DEFAULT '0',
  `digg` int(10) unsigned NOT NULL,
  `comments` int(10) unsigned NOT NULL DEFAULT '0',
  `follows` int(10) unsigned NOT NULL DEFAULT '0',
  `counter` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '公用计数器，如相册下图片总数、下载次数等',
  `ischeck` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `publish` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `delflag` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `sid` char(50) DEFAULT NULL,
  `cid` char(50) NOT NULL,
  `subject` char(150) NOT NULL,
  `subtitle` char(100) NOT NULL,
  `keywords` char(200) NOT NULL,
  `description` char(200) NOT NULL,
  `alias` char(150) NOT NULL,
  `extlink` char(150) NOT NULL,
  `coverpic` char(150) NOT NULL,
  `attrib` char(20) NOT NULL,
  `sortby` smallint(5) unsigned NOT NULL DEFAULT '0',
  `tplfile` char(100) NOT NULL,
  `createtime` int(10) unsigned NOT NULL,
  `pubtime` int(10) unsigned NOT NULL,
  `updatetime` int(10) unsigned NOT NULL,
  `match_sid` char(40) NOT NULL,
  `match_cid` char(100) NOT NULL,
  `match_attrib` char(40) NOT NULL,
  `match_search` char(255) NOT NULL,
  `old_id` int(10) unsigned NOT NULL,
  PRIMARY KEY (`aid`,`module_id`,`channel_id`),
  KEY `idx_ipd` (`ischeck`,`publish`,`delflag`,`sortby`),
  KEY `idx_mcu` (`uid`,`root_uid`),
  FULLTEXT KEY `ft_cid` (`match_cid`),
  FULLTEXT KEY `ft_search` (`match_search`),
  FULLTEXT KEY `ft_attrib` (`match_attrib`),
  FULLTEXT KEY `ft_sid` (`match_sid`),
  FULLTEXT KEY `ft_sc` (`match_sid`,`match_cid`)
) ENGINE=MyISAM AUTO_INCREMENT=10619 DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `th_attachments`
-- ----------------------------
DROP TABLE IF EXISTS `th_attachments`;
CREATE TABLE `th_attachments` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `uid` int(10) unsigned NOT NULL,
  `aid` int(10) unsigned NOT NULL,
  `cid` smallint(5) unsigned NOT NULL DEFAULT '0',
  `sid` smallint(5) unsigned NOT NULL,
  `channel_id` smallint(5) unsigned NOT NULL,
  `filepath` char(255) NOT NULL COMMENT '原始图片路径',
  `filetype` char(50) NOT NULL,
  `description` char(255) NOT NULL,
  `filesize` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `createtime` int(10) unsigned NOT NULL,
  PRIMARY KEY (`id`),
  KEY `uid` (`uid`),
  KEY `filepath` (`filepath`),
  KEY `idx_sca` (`sid`,`channel_id`,`aid`)
) ENGINE=MyISAM AUTO_INCREMENT=108 DEFAULT CHARSET=utf8;

