-- ----------------------------
--  Table structure for `th_blocks`
-- ----------------------------
DROP TABLE IF EXISTS `th_blocks`;
CREATE TABLE `th_blocks` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `uid` int(10) unsigned NOT NULL,
  `sid` smallint(5) unsigned NOT NULL,
  `cityid` smallint(5) unsigned NOT NULL,
  `publish` tinyint(1) unsigned NOT NULL,
  `pid` smallint(5) unsigned NOT NULL,
  `prefix` char(30) NOT NULL,
  `block_name` char(50) NOT NULL,
  `data_type` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '0=普通文本 1=列表内容 2=智能获取',
  `content` text NOT NULL,
  `createtime` int(10) unsigned NOT NULL,
  `updatetime` int(10) unsigned NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `unq_prefix_cityId` (`prefix`,`cityid`)
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

