-- ----------------------------
DROP TABLE IF EXISTS `th_channel_fields`;
CREATE TABLE `th_channel_fields` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `mid` smallint(5) unsigned NOT NULL,
  `fid` smallint(5) unsigned NOT NULL,
  `alias_name` char(50) NOT NULL DEFAULT '',
  `label` char(50) NOT NULL,
  `function` char(50) NOT NULL,
  `sort` smallint(5) unsigned NOT NULL DEFAULT '0',
  `group` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `settings` text NOT NULL,
  `is_system` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `is_enable` tinyint(1) unsigned NOT NULL DEFAULT '1',
  `is_batch` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `is_list` char(3) NOT NULL DEFAULT '0',
  `is_search` char(10) NOT NULL DEFAULT '0',
  `is_sort` char(20) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

