-- ----------------------------
--  Table structure for `th_common_arcindex`
-- ----------------------------
DROP TABLE IF EXISTS `th_common_arcindex`;
CREATE TABLE `th_common_arcindex` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `sid` smallint(5) DEFAULT NULL,
  `channel_id` smallint(5) unsigned NOT NULL,
  `cid` char(30) NOT NULL,
  `subject` char(120) NOT NULL,
  `subtitle` char(130) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_sc` (`sid`,`channel_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

