-- ----------------------------
--  Table structure for `th_common_category`
-- ----------------------------
DROP TABLE IF EXISTS `th_common_category`;
CREATE TABLE `th_common_category` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `sid` smallint(5) unsigned NOT NULL,
  `channel_id` smallint(5) unsigned NOT NULL DEFAULT '0',
  `pid` int(10) unsigned NOT NULL DEFAULT '0',
  `rootid` int(10) unsigned NOT NULL DEFAULT '0',
  `depth` tinyint(8) unsigned NOT NULL DEFAULT '1',
  `sortby` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `child_count` smallint(5) unsigned NOT NULL DEFAULT '0',
  `node` char(200) NOT NULL,
  `catename` char(200) NOT NULL,
  `alias` char(100) NOT NULL,
  `keywords` char(100) NOT NULL,
  `description` char(255) NOT NULL,
  `coverpic` char(150) NOT NULL,
  `link` char(100) NOT NULL,
  `tpltype` enum('index','list','page') NOT NULL DEFAULT 'list',
  `setting_key` text NOT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_prn` (`pid`,`rootid`,`node`),
  KEY `idx_sc` (`sid`,`channel_id`)
) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `th_common_fields`
-- ----------------------------
DROP TABLE IF EXISTS `th_common_fields`;
CREATE TABLE `th_common_fields` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT,
  `group_id` tinyint(3) unsigned NOT NULL,
  `sortby` tinyint(3) unsigned NOT NULL,
  `is_enable` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `is_batch` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `is_list` char(3) NOT NULL DEFAULT '0',
  `is_search` char(20) NOT NULL DEFAULT '',
  `is_sort` char(20) NOT NULL DEFAULT '',
  `module_key` char(20) NOT NULL,
  `alias_name` char(50) NOT NULL,
  `field_name` char(50) NOT NULL DEFAULT '',
  `label` char(50) NOT NULL,
  `tips` char(200) NOT NULL,
  `type` char(40) NOT NULL,
  `attr` char(200) NOT NULL,
  `value` char(100) NOT NULL,
  `option` char(200) NOT NULL,
  `function` char(50) NOT NULL,
  `settings` text NOT NULL,
  PRIMARY KEY (`id`),
  KEY `module_key` (`module_key`)
) ENGINE=MyISAM AUTO_INCREMENT=23 DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `th_common_related`
-- ----------------------------
DROP TABLE IF EXISTS `th_common_related`;
CREATE TABLE `th_common_related` (
  `channel_id` int(11) DEFAULT NULL,
  `aid` int(11) DEFAULT NULL,
  `related_channel_id` int(11) DEFAULT NULL,
  `related_aid` int(11) DEFAULT NULL,
  KEY `idx_aid` (`channel_id`,`aid`),
  KEY `idx_related_aid` (`related_channel_id`,`related_aid`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

