DROP TABLE IF EXISTS `th_downloads`;
CREATE TABLE `th_downloads` (
  `aid` int(10) unsigned NOT NULL,
  `sid` smallint(5) unsigned NOT NULL,
  `channel_id` smallint(5) unsigned NOT NULL,
  `cid` char(100) NOT NULL,
  `filepath` char(200) NOT NULL,
  `filesize` int(10) unsigned NOT NULL,
  `dlcount` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '下载次数',
  PRIMARY KEY (`aid`,`sid`,`channel_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

