-- ----------------------------
--  Table structure for `th_events`
-- ----------------------------
DROP TABLE IF EXISTS `th_events`;
CREATE TABLE `th_events` (
  `aid` int(10) unsigned NOT NULL,
  `sid` smallint(5) unsigned NOT NULL,
  `channel_id` smallint(5) unsigned NOT NULL,
  `cid` char(100) NOT NULL,
  `tid` char(100) NOT NULL,
  `events_name` char(255) NOT NULL,
  `run_type` tinyint(3) unsigned NOT NULL,
  `run_days` char(60) NOT NULL,
  `run_week` char(14) NOT NULL,
  `vid` char(150) NOT NULL,
  `phone` char(100) NOT NULL,
  `bdate` date NOT NULL,
  `edate` date NOT NULL,
  `run_status` char(3) NOT NULL DEFAULT '1',
  `next_date` char(7) NOT NULL,
  `region` char(150) NOT NULL,
  `match_tid` char(255) NOT NULL,
  `match_date` text NOT NULL,
  `match_vid` char(255) NOT NULL,
  PRIMARY KEY (`aid`),
  KEY `run_type` (`run_type`),
  KEY `run_status` (`run_status`,`next_date`),
  KEY `idx_mc` (`sid`,`channel_id`),
  FULLTEXT KEY `match_vid` (`match_vid`),
  FULLTEXT KEY `match_date` (`match_date`)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `th_family_arcindex`
