-- ----------------------------
--  Table structure for `th_family_category`
-- ----------------------------
DROP TABLE IF EXISTS `th_family_category`;
CREATE TABLE `th_family_category` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `sid` smallint(5) unsigned NOT NULL,
  `channel_id` smallint(5) unsigned NOT NULL DEFAULT '0',
  `pid` int(10) unsigned NOT NULL DEFAULT '0',
  `rootid` int(10) unsigned NOT NULL DEFAULT '0',
  `depth` tinyint(8) unsigned NOT NULL DEFAULT '1',
  `sortby` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `child_count` smallint(5) unsigned NOT NULL DEFAULT '0',
  `node` char(200) NOT NULL,
  `catename` char(200) NOT NULL,
  `alias` char(100) NOT NULL,
  `keywords` char(100) NOT NULL,
  `description` char(255) NOT NULL,
  `coverpic` char(150) NOT NULL,
  `link` char(100) NOT NULL,
  `tpltype` enum('index','list','page') NOT NULL DEFAULT 'list',
  `setting_key` text NOT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_prn` (`pid`,`rootid`,`node`),
  KEY `idx_sc` (`sid`,`channel_id`)
) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

