-- ----------------------------
--  Table structure for `th_modules`
-- ----------------------------
DROP TABLE IF EXISTS `th_modules`;
CREATE TABLE `th_modules` (
  `id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `is_sysmod` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `is_enable` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `def_module` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `prefix` char(30) NOT NULL,
  `module_name` char(50) NOT NULL,
  `category_table` char(20) NOT NULL,
  `index_table` char(20) NOT NULL,
  `settings` text NOT NULL,
  `site_config` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `th_permissions`
-- ----------------------------
DROP TABLE IF EXISTS `th_permissions`;
CREATE TABLE `th_permissions` (
  `id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `sortby` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `pid` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '上级权限ID',
  `groupid` enum('admin','user') NOT NULL DEFAULT 'admin' COMMENT '管理/会员权限',
  `isshow` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否在菜单显示',
  `prefix` char(50) NOT NULL COMMENT '唯一标识',
  `pname` char(50) NOT NULL COMMENT '权限名称',
  `settings` text NOT NULL COMMENT '其它设定',
  `icon_class` char(20) NOT NULL COMMENT '图标类名',
  `form_type` char(20) NOT NULL,
  `form_attr` char(255) NOT NULL,
  `form_value` char(200) NOT NULL,
  `form_option` char(255) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `pp` (`pid`,`prefix`)
) ENGINE=MyISAM AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `th_plugins`
-- ----------------------------
DROP TABLE IF EXISTS `th_plugins`;
CREATE TABLE `th_plugins` (
  `id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `prefix` char(30) NOT NULL,
  `plugin_name` char(50) NOT NULL,
  `do_action` text NOT NULL,
  `is_enable` tinyint(4) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `th_search_index`
-- ----------------------------
DROP TABLE IF EXISTS `th_search_index`;
CREATE TABLE `th_search_index` (
  `aid` int(10) unsigned NOT NULL,
  `channel_id` smallint(5) unsigned NOT NULL,
  `sid` smallint(5) unsigned NOT NULL,
  `subject` char(150) NOT NULL,
  `subtitle` char(100) NOT NULL,
  `keywords` char(200) NOT NULL,
  `description` char(255) NOT NULL,
  `search_text` text NOT NULL,
  `search_text_ft` text NOT NULL,
  `createtime` int(10) unsigned NOT NULL,
  `updatetime` int(10) unsigned NOT NULL,
  PRIMARY KEY (`aid`,`sid`,`channel_id`),
  FULLTEXT KEY `ft_search_text` (`search_text_ft`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

-- ----------------------------
--  Records of `th_search_index`
-- ----------------------------
BEGIN;
INSERT INTO `th_search_index` VALUES ('1', '1', '1', 'Walk This Way: Family-Friendly Tours', '', 'Shanghai, walking tour, family', 'Bogged down by the daily grind, it is all too easy to take the same old sights every day for granted. A walking tour provides a great way to discover or rediscover parts of the city that might otherwise be forgotten.', '', '', '1450113197', '1398745679'), ('2', '1', '1', 'The Homework Homestretch', '', 'education, learning, study habits, New Pathway', 'Implementing these study tips will not only help students gain the most from their studies, they will also ensure that students set and develop good habits for college and beyond.', '', '', '1450113197', '1398753419'), ('336', '1', '1', 'Weekly Event Roundup 11.25-12.1', '', 'events, november, thanksgiving', 'Here is the roundup of events coming this week!', '', '', '1450113212', '1448419554'), ('670', '1', '1', 'Urbanites Frozen Party', '', 'urban family, urban family guangzhou, guangzhou, urbanites, urbanites guangzhou, prd, frozen, disney, frozen movie, disney frozen, canton club, canton club guangzhou, parkview, parkview guangzhou, loa', 'On May 31, 70 princess Elsa’s and their families enjoyed the Urbanites Frozen party held by Urban Family magazine and The Canton Club.', '', '', '1450956640', '0'), ('3', '1', '1', 'Lead Poisoning Prevention: Limit Your Child\'s Exposure', '', 'wellbeing, lead poisoning, child, health, China', 'Lead poisoning is a serious threat to children’s health. Children’s growing bodies absorb more lead than adults and lead exposure can leave lasting damage to a child’s developing nervous system and other organs.', '', '', '1450113197', '1398753333'), ('4', '1', '1', 'Back To Basics: Feeding a Family in Shanghai', '', 'food-fun, eat-well, family nutrition, Shanghai-wet-market', 'For a veteran Shanghai expat like me it is truly remarkable how far the grocery experience has evolved here in Shanghai.\r\n\r\nWhen I arrived here in 1996, food delivery meant you sent the ayi to the wet market and she brought the vegetables back to your hou', '', '', '1450113197', '1398747528'), ('5', '1', '1', 'Brushing Up With Fluoride', '', 'wellbeing, fluoride, toothbrush, cavity-prevention', 'Fluoride is an essential part of children’s dental health, helping to make your kids’ teeth stronger, which prevents decay. Nothing makes a parent prouder than hearing their child come from the dentist’s office flashing their pearly whites declaring, “No', '', '', '1450113197', '1397704071'), ('6', '1', '1', 'Kid-Friendly Libraries in Shanghai', '', 'shanghai-library, english books, kid-friendly', 'For many foreign families in Shanghai, public libraries are one of the things kids and parents miss most from back home. While you won\'t find the same variety of books and activities in Shanghai, we found three libraries worth checking out.', '', '', '1450113197', '1398754566'), ('7', '1', '1', '5 easy steps for healthier air and water', '', 'wellbeing', 'Don\'t let the sometimes shocking news about China\'s air and water quality to scare you away. Follow these simple solutions to ensure your family\'s safety.', '', '', '1450113197', '1398747577'), ('8', '1', '1', 'Trains, Planes and Automobiles - Getting from A to B', '', 'new-to-shanghai', 'The Urban Family run down on getting from A to B in Shanghai', '', '', '1450113197', '1451458629'), ('9', '1', '1', 'Hiring Help: Ayis and drivers to the rescue', '', 'life-culture', 'Our tips for getting the best help in Shanghai', '', '', '1450113197', '1398066457'), ('10', '1', '1', 'Settle Down: The where, what and why of renting in Shanghai', '', 'new-to-Shanghai', 'Shanghai is a huge metropolis and figuring out where to live, work and play can be a daunting task for new expats. Here\'s a breakdown of the various areas and ins and outs of each.', '', '', '1450113197', '1397810966'), ('11', '1', '1', 'Another Test Post', '', 'testing, test', 'Here is a description of the  test article. Editing the test post.', '', '', '1450113197', '1409031983'), ('12', '1', '1', 'Shanghai\'s top summer camps', '', 'lifestyle', 'Warm weather and school holidays are nearing the horizon so Urban Family\'s rounded up the best summer camps in Shanghai to keep your little (or larger!) ones occupied.', '', '', '1450113197', '1398916694'), ('13', '1', '1', 'Shanghai\'s Bird and Flower Markets', '', 'family, markets, bird, flowers, shopping, things-to-do', 'Among the best things about living in Shanghai are the bird and flower markets that scattered around town, carry much more than just birds and flowers.', '', '', '1450113197', '1398759792'), ('14', '1', '1', 'Shanghai Quiz:  Test Your Local Knowledge', '', 'shanghai, fun facts, quiz', 'Think you know everything there is to know about Shanghai? Try our quiz full of fun facts.', '', '', '1450113197', '1398762026'), ('15', '1', '1', 'Just For Fun: How Local Are You?', '', 'quiz, Shanghai, parents, expats, kids', 'Questions to test just how much influence China has had on you.', '', '', '1450113197', '1401260665'), ('16', '1', '1', 'Shanghai\'s best: Mother\'s Day Dining', '', 'eat-drink', 'Moms deserve a special treat this Mother\'s Day, we\'ve hunted down the best deals in town', '', '', '1450113197', '1399360687'), ('17', '1', '1', 'Kids in the Kitchen: Perfect Pancakes', '', 'food, kids, pancake', 'How to turn a pancake into something special this Mother\'s Day', '', '', '1450113197', '1399280811'), ('18', '1', '1', 'Art Smart: Make a Monet inspired collage', '', 'art', 'Children are never too young to appreciate art. Using art history as her muse, every issue Colorbox Arts creator Monika Lin provides a project that can be done at home and incorporates a brief lesson.', '', '', '1450113197', '1399279498'), ('19', '1', '1', '\'Almost half\' of urban Chinese kids using social media', '', 'life-culture', 'Over 50 percent of Chinese children living in cities aged between 6-12 have social media or microblog accounts, the Global Times reports.', '', '', '1450113197', '1399363228'), ('20', '1', '1', 'China tightening control on imported baby powder', '', 'wellbeing', 'China has tightened controls on imports of infant formula', '', '', '1450113197', '1399447729'), ('21', '1', '1', 'Students hacked into college website to help classmates skip PE', '', 'learning', 'Two college students have been arrested after allegedly being paid by classmates to hack into the school\'s website to alter morning run records, Shanghai Daily reports.', '', '', '1450113197', '1399615473'), ('22', '1', '1', 'School House Rock at Concordia!', '', 'school, things to do', 'Get ready to boogie at Concordia\'s latest production', '', '', '1450113197', '1400051902'), ('23', '1', '1', 'C-Sections still favored by women giving birth in Shanghai', '', 'wellbeing', 'The Chinese government wants more women to undergo natural childbirth, but many mothers-to-be still prefer caesarean sections according to new figures', '', '', '1450113197', '1399864630'), ('24', '1', '1', 'Men undergo \'simulated childbirth\' for Mother\'s Day', '', 'wellbeing', 'Volunteers in Nanchang, Jiangxi Province underwent simulated labour contractions Sunday to experience the pain of childbirth,', '', '', '1450113197', '1399959954'), ('25', '1', '1', 'New IB Career-Related Program at SSIS', '', 'SSIS, education, learning, IBCC, IB, Shanghai', 'The Shanghai Singapore International School (SSIS) just received its candidacy status for the new International Baccalaureate Career-related Certificate (IBCC). It is the first school in China to offer this new program for grades 11 and 12.', '', '', '1450113197', '1399949283'), ('26', '1', '1', 'Shanghais\' best bookstores for kids', '', 'books, learning', 'Top Picks for the best bookstores Shanghai has to offer', '', '', '1450113197', '1399961866'), ('27', '1', '1', 'Traditional  Chinese Medicine and your child', '', 'wellbeing', 'When should a child see a Traditional Chinese Medicine doctor?', '', '', '1450113197', '1399963170'), ('28', '1', '1', 'Can TCM Cure Childhood Asthma?', '', 'wellbeing, TCM, health, children, Shanghai', 'Asthma comes in different forms and levels. While Western medicine merely controls attacks, Traditional Chinese Medicine (TCM) can cure the root of the asthma. Even in the most severe cases, a reduction in symptoms can usually be achieved.', '', '', '1450113197', '1400047005'), ('29', '1', '1', 'Stretch and Connect With Family Yoga', '', 'wellbeing, health, yoga, family, Shanghai', 'Whether you are an experienced yogi or have never done a downward dog, a family yoga practice can help parents and children unwind and connect.', '', '', '1450113197', '1400047647'), ('30', '1', '1', 'UHT vs Fresh: Which milk does the body good?', '', 'eat-well, food, milk-safety', 'From a nutritional perspective, all cow’s milk is basically the same for major nutrients; it is in the processing of milk that questions arise.', '', '', '1450113197', '1400046908'), ('31', '1', '1', 'Fruit and Veg: Wash the pesticides away', '', 'eat-drink, wellbeing, family, health, vegetables, fruit, pesticides', 'Food safety is one of the top concerns of parents in Shanghai. One way to take control of food safety is learning how to properly wash your fruits and vegetables.', '', '', '1450113197', '1400565118'), ('32', '1', '1', 'Fit Families: Where to break a sweat in the city', '', 'sport, family, wellbeing', 'Don\'t let the fast pace of Shanghai slow down your fitness. Stay in tip-top shape, whichever way you- and your kids prefer. Check out our favorite Shanghai sporty spots.', '', '', '1450113197', '1400568703'), ('33', '1', '1', 'Two Shanghai women making a difference in Kenya', '', 'lifestyle', 'Not happy sitting on the sidelines, two Shanghai-based authors made a trip to Nakuru, Kenya in an effort to try to make a difference.', '', '', '1450113197', '1400654509'), ('34', '1', '1', 'One child policy worsens midwife shortage', '', 'midwives, wellbeing, birth, one child policy', 'A change in China\'s one 