-- ----------------------------
--  Table structure for `th_setting`
-- ----------------------------
DROP TABLE IF EXISTS `th_setting`;
CREATE TABLE `th_setting` (
  `groupid` tinyint(4) unsigned NOT NULL,
  `sortby` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `module` char(10) NOT NULL,
  `sgroup` char(30) NOT NULL,
  `setting_key` char(30) NOT NULL,
  `setting_value` char(255) NOT NULL,
  `label` char(30) NOT NULL,
  `tips` char(255) NOT NULL,
  `eletype` enum('input:text','input:checkbox','input:radio','input:email','input:number','select','select:[label]','textarea') NOT NULL,
  `option` char(200) NOT NULL,
  `attr` char(255) NOT NULL,
  `valuetype` enum('array','string','boolean') NOT NULL DEFAULT 'string',
  `is_enable` tinyint(1) unsigned NOT NULL DEFAULT '1',
  UNIQUE KEY `groupid_key` (`groupid`,`setting_key`),
  KEY `module` (`module`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

-- ----------------------------
--  Table structure for `th_sites`
-- ----------------------------
DROP TABLE IF EXISTS `th_sites`;
CREATE TABLE `th_sites` (
  `id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `is_enable` tinyint(1) unsigned NOT NULL DEFAULT '1',
  `is_def` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `prefix` char(20) NOT NULL,
  `domain` char(50) NOT NULL,
  `site_name` char(30) NOT NULL,
  `category_table` char(30) NOT NULL,
  `arcindex_table` char(30) NOT NULL,
  `use_channel` char(50) NOT NULL,
  `keywords` char(200) NOT NULL,
  `description` char(200) NOT NULL,
  `setting_key` text NOT NULL,
  `site_config` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

