-- ----------------------------
--  Table structure for `th_treedata`
-- ----------------------------
DROP TABLE IF EXISTS `th_treedata`;
CREATE TABLE `th_treedata` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `pid` int(10) unsigned NOT NULL DEFAULT '0',
  `tid` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `rootid` int(10) unsigned NOT NULL DEFAULT '0',
  `node` char(200) NOT NULL,
  `depth` mediumint(8) unsigned NOT NULL DEFAULT '1',
  `dataname` char(200) NOT NULL,
  `sortby` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `alias` char(100) NOT NULL,
  `child_count` smallint(5) unsigned NOT NULL DEFAULT '0',
  `setting_key` text NOT NULL,
  PRIMARY KEY (`id`),
  KEY `pid` (`pid`,`rootid`,`node`),
  KEY `alias` (`alias`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

