-- ----------------------------
--  Table structure for `th_user_infos`
-- ----------------------------
DROP TABLE IF EXISTS `th_user_infos`;
CREATE TABLE `th_user_infos` (
  `uid` int(10) unsigned NOT NULL DEFAULT '0',
  `realname` char(50) NOT NULL COMMENT '姓名',
  `gender` enum('0','1','2') NOT NULL COMMENT '性别',
  PRIMARY KEY (`uid`),
  KEY `gender` (`gender`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

